mg {\\n            width: 100%;\\n            height: 100%;\\n            object-fit: cover;\\n        }\\n\\n        .slide-content {\\n            position: absolute;\\n            bottom: 20%;\\n            left: 10%;\\n            color: white;\\n            text-shadow: 2px 2px 5px rgba(0, 0, 0, 0.5);\\n            max-width: 500px;\\n        }\\n\\n        .slide-content h2 {\\n            font-size: 2.5rem;\\n            margin-bottom: 15px;\\n        }\\n\\n        .slide-content p {\\n            font-size: 1.2rem;\\n            margin-bottom: 20px;\\n        }\\n\\n        .slider-nav {\\n            position: absolute;\\n            bottom: 20px;\\n            left: 50%;\\n            transform: translateX(-50%);\\n            display: flex;\\n            gap: 10px;\\n        }\\n\\n        .slider-dot {\\n            width: 12px;\\n            height: 12px;\\n            border-radius: 50%;\\n            background-color: rgba(255, 255, 255, 0.5);\\n            cursor: pointer;\\n            transition: var(--transition);\\n        }\\n\\n        .slider-dot.active {\\n            background-color: white;\\n            transform: scale(1.2);\\n        }\\n\\n        .slider-arrows {\\n            position: absolute;\\n            top: 50%;\\n            width: 100%;\\n            display: flex;\\n            justify-content: space-between;\\n            padding: 0 20px;\\n            transform: translateY(-50%);\\n        }\\n\\n        .slider-arrow {\\n            background-color: rgba(255, 255, 255, 0.7);\\n            width: 40px;\\n            height: 40px;\\n            border-radius: 50%;\\n            display: flex;\\n            align-items: center;\\n            justify-content: center;\\n            cursor: pointer;\\n            transition: var(--transition);\\n        }\\n\\n        .slider-arrow:hover {\\n            background-color: white;\\n        }\\n\\n        \\/* Products Section *\\/\\n        .section-title {\\n            text-a