: flex;\\n            justify-content: center;\\n            gap: 20px;\\n            margin-bottom: 20px;\\n            flex-wrap: wrap;\\n        }\\n\\n        .footer-links a {\\n            color: #ddd;\\n            text-decoration: none;\\n            transition: var(--transition);\\n        }\\n\\n        .footer-links a:hover {\\n            color: white;\\n        }\\n\\n        .social-icons {\\n            display: flex;\\n            justify-content: center;\\n            gap: 15px;\\n            margin-bottom: 20px;\\n        }\\n\\n        .social-icons a {\\n            color: white;\\n            font-size: 1.2rem;\\n            transition: var(--transition);\\n        }\\n\\n        .social-icons a:hover {\\n            color: var(--secondary-color);\\n        }\\n\\n        .copyright {\\n            color: #aaa;\\n            font-size: 0.9rem;\\n        }\\n\\n        \\/* Responsive Design *\\/\\n        @media (max-width: 992px) {\\n            .slider-container {\\n                height: 50vh;\\n            }\\n            \\n            .slide-content h2 {\\n                font-size: 2rem;\\n            }\\n            \\n            .flex-container, .about-container {\\n                flex-direction: column;\\n            }\\n            \\n            .flex-image, .about-image {\\n                width: 100%;\\n            }\\n        }\\n\\n        @media (max-width: 768px) {\\n            .slider-container {\\n                height: 40vh;\\n                min-height: 300px;\\n            }\\n            \\n            .slide-content {\\n                bottom: 10%;\\n                left: 5%;\\n            }\\n            \\n            .slide-content h2 {\\n                font-size: 1.5rem;\\n            }\\n            \\n            .slide-content p {\\n                font-size: 1rem;\\n            }\\n            \\n            .section-title {\\n                font-size: 1.7rem;\\n            }\\n            \\n            .