ial-icons {\n            display: flex;\n            justify-content: center;\n            gap: 15px;\n            margin-bottom: 20px;\n        }\n\n        .social-icons a {\n            color: white;\n            font-size: 1.2rem;\n            transition: var(--transition);\n        }\n\n        .social-icons a:hover {\n            color: var(--secondary-color);\n        }\n\n        .copyright {\n            color: #aaa;\n            font-size: 0.9rem;\n        }\n\n        /* Responsive Design */\n        @media (max-width: 992px) {\n            .slider-container {\n                height: 50vh;\n            }\n            \n            .slide-content h2 {\n                font-size: 2rem;\n            }\n            \n            .flex-container, .about-container {\n                flex-direction: column;\n            }\n            \n            .flex-image, .about-image {\n                width: 100%;\n            }\n        }\n\n        @media (max-width: 768px) {\n            .slider-container {\n                height: 40vh;\n                min-height: 300px;\n            }\n            \n            .slide-content {\n                bottom: 10%;\n                left: 5%;\n            }\n            \n            .slide-content h2 {\n                font-size: 1.5rem;\n            }\n            \n            .slide-content p {\n                font-size: 1rem;\n            }\n            \n            .section-title {\n                font-size: 1.7rem;\n            }\n            \n            .flex-content, .about-content {\n                padding: 30px;\n            }\n        }\n\n        @media (max-width: 576px) {\n            .slider-container {\n                height: 35vh;\n                min-height: 250px;\n            }\n            \n            .slide-content h2 {\n                font-size: 1.3rem;\n            }\n            \n            .products-grid {\n                grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));\n            }\n            \n            .feature-card {\n                padding: 20px;\n            }\n        }\n    </style>\n</head>\n<body>\n    <!-- Slider Section -->\n    <section class=\"slider-container\">\n        <div class=\"slider\">\n            <div class=\"slide\">\n                <img src=\"https://images.unsplash.com/photo-1607082348824-0a96f2a4b9da?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1470&q=80\" alt=\"Fashion Collection\">\n                <div class=\"slide-content\">\n                    <h2>New Summer Collection</h2>\n                    <p>Discover the latest trends in fashion for this season</p>\n                    <a href=\"#\" class=\"btn\">Shop Now</a>\n                </div>\n            </div>\n            <div class=\"slide\">\n                <img src=\"https://images.unsplash.com/photo-1441986300917-64674bd600d8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1470&q=80\" alt=\"Electronics\">\n                <div class=\"slide-content\">\n                    <h2>Tech Gadgets Sale</h2>\n                    <p>Up to 30% off on the latest electronics</p>\n                    <a href=\"#\" class=\"btn\">Explore Deals</a>\n                </div>\n            </div>\n            <div class=\"slide\">\n                <img src=\"https://images.unsplash.com/photo-1586023492125-27b2c045efd7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1558&q=80\" alt=\"Home Decor\">\n                <div class=\"slide-content\">\n                    <h2>Home Makeover</h2>\n                    <p>Refresh your space with our home decor collection</p>\n                    <a href=\"#\" class=\"btn\">Discover More</a>\n                </div>\n            </div>\n            <div class=\"slide\">\n                <img src=\"https://images.unsplash.com/photo-1601924994987-69e26d50dc96?ixlib=rb-4.0.3&ixid=